import React from 'react';
import { useForm } from "react-hook-form";
import { CustomInput, CustomInputFile, Select } from 'components';
import { arrayCity, arrayTime } from 'utils';
import { FormContainer, InputName, InputDescription, InputPrice, InputTime, InputImage, Span } from './styled';
const Component = () => {
    const { register, errors, handleSubmit } = useForm();

    const onSubmit = (data, e) => {
        alert(JSON.stringify(data));
        e.target.reset()
    };
    return (
        <FormContainer onSubmit={handleSubmit(onSubmit)} >
            <InputName>
                <CustomInput
                    type={"string"}
                    placeholder={"Ej. Bocina Bluetooth"}
                    label="Nombre del producto"
                    {...register("name", { required: { value: true, message: "El nombre es requerido" } })}
                />
                <Span>hoo</Span>
            </InputName>
            <InputDescription>
                <CustomInput
                    type={"string"}
                    placeholder={"Ej. Bocina Bluetooth "}
                    label={"Descripción del producto"}
                    {...register("description")}
                />
            </InputDescription>
            <InputPrice>
                <CustomInput
                    type="number"
                    isRequired={true}
                    label={"Precio por día"}
                    {...register("price")}
                />
            </InputPrice>
            <InputTime>
                <Select label={"¿En cuánto tiempo puedes entregar el producto?"}
                    options={arrayTime}
                    {...register("time")}
                />
            </InputTime>
            <InputTime>
                <Select label={"¿En dónde tienes el producto?"}
                    options={arrayCity}
                    {...register("location")}
                />
            </InputTime>
            <InputImage>
                <CustomInputFile />
            </InputImage>
            <input type="submit" />
        </FormContainer>
    );
}

export default Component;
export { Component as Form }
